INSERT INTO `t_project` VALUES ('18', '南京航空航天大学', 'NULL', null, null, null, 'NULL', '磐石_客户端', '项目产品: 磐石服务器版本号3.0.30  客户端版本v3.0(29.26)\n      客户端登录的时候证书显示不出来，使用无KEY登录码登陆时报错。\n使用PE盘跳过磐石后，用户名密码登录进系统，桌面不能正常显示，使用任务管理器添加explorer后桌面仍然无法显示，只能重做系统。(客户端系统为Windows XPsp3)\n      客户端陆续出现该问题，已经有五台计算机出现该情况。附件为其中一台计算机的日志，昨天出现的问题，请查看一下日志。如有其它信息需要了解请联系我。\n', '2015-01-16 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '现场排查，用户系统问题', 'NULL', 'NULL', '2015-01-16 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
