INSERT INTO `t_project` VALUES ('64', '兵器617', 'NULL', null, null, null, 'NULL', '网关_代理', '兵器617项目用户反馈，617厂研发部的惠普工作站部分机型（Z600、6600、6400）安装网关客户端后，网速不正常，变成几KB每秒。正常应该是10M/s以上，安装时没有报错。\n其中安装了最新版本客户端（PNXSetup_3.00.0135）后，4600型号恢复正常。其他两款型号问题依旧。\n      现场所有终端操作系统均安装的Win7x64位，专业版和旗舰版都有，其他正常的机型还有（z620、z640、z440、z210、z200）。\n请研发同事帮助分析一下，问题原因。\n', '2015-03-10 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', '2015-03-10 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
