INSERT INTO `t_project` VALUES ('67', '南京晨光集团', 'NULL', null, null, null, 'NULL', '网关_服务器', '南京晨光项目使用G3000-E网关，现客户漏扫出安全漏洞，参见附件；\n \n现网关版本服务端为3.0.6.4，请确认一下我们是否已经有相应的解决方案。\n', '2015-03-13 00:00:00', '已完成', '代艳明', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '升级网关，不适用sslv3', 'NULL', 'NULL', '2015-03-13 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
INSERT INTO `t_project` VALUES ('68', '江西CA', 'NULL', null, null, null, 'NULL', '网关_移动', '江西CA将开展移动业务，近期会有项目落地，应用模式为E网关与蓝牙KEY的结合。\n \n江西CA现确定项目中采用龙脉KEY（支持双算法），测试蓝牙KEY已经提供。\n \n客户要求先行完成RSA证书与网关的结合，后期随着SM2证书的推广，再行实现SM2证书的发放、网关的结合。\n \n江西CA是我们长期的合作伙伴，随着移动业务后期拓展，我们的网关产品将会有更多的售卖机会。\n \n请评估并协调尽快处理。\n', '2015-03-13 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '新需求', 'NULL', 'NULL', '2015-03-13 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
INSERT INTO `t_project` VALUES ('69', '兵器210研究所', 'NULL', null, null, null, 'NULL', '网关_服务器', '陕西210研究所测评中提出，T600审计信息中对用户IP地址的记录为网关的入\n口地址，他们要求能显示用户真实地址。\n  他们近期需要换咱们的G3000-e，我看好像也无法记录用户的真实地址。关于这个怎\n么方便给用户回复？谢谢\n', '2015-03-16 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', '2015-03-16 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
INSERT INTO `t_project` VALUES ('70', '山东监狱管理局', 'NULL', null, null, null, 'NULL', '网关_服务器', '项目遇到无法正常显示SM2证书问题，我已指导配置相关KEY类型信息等方案。但是仍然不能正确读取到中孚KEY中的SM2证书，请排查一下原因', '2015-03-16 00:00:00', '已完成', '代艳明', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '管理端支持sm2证书，需要修改平台文件', 'NULL', 'NULL', '2015-03-16 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
INSERT INTO `t_project` VALUES ('71', '西南', 'NULL', null, null, null, 'NULL', '网关_客户端', '西南的王鹏：有台机器装了E网关客户端后不正常，用客户端修复工具检查发现网络组件未正常运行，这个压缩包是客户端的debug日志拍照（军工单位拷出文件现在办不了手续），日志内容没有拍漏。文件内有些密文，为了拍照我删掉了几行。这台机器无论怎么重装网关客户端，都是网络组件有问题。', '2015-03-16 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '网卡驱动问题', 'NULL', 'NULL', '2015-03-16 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
INSERT INTO `t_project` VALUES ('72', '沧州市公安局边界项目', '公安', null, null, null, 'NULL', '网关_客户端', '客户端登陆网关后十几分钟后就会出现域名访问的应用无法登陆，ip地址访问的应用没问题，需要退出网关重新登陆才能继续登陆，但是十几分钟后又不能登陆域名的应用了', '2015-03-17 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '网关bug，域名应用都有此问题，已修正', 'NULL', 'NULL', '2015-03-17 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
