INSERT INTO `t_project` VALUES ('255', '云南电网', 'NULL', null, null, null, '3.0.69.9', '网关_服务器', '错误日志导出后，错误描述显示“乱码”', '2015-10-26 00:00:00', '已完成', '代艳明', '62618866', '2015-10-26 00:00:00', '0', '疏治', 'NULL', '错误描述为BASE64编码的内容，解码后可查看，并非乱码。问题结束。\n后续可能需要提供统一解码工具。', 'NULL', 'NULL', '2015-10-26 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
INSERT INTO `t_project` VALUES ('256', '文物局', 'NULL', null, null, null, 'NULL', '网关_客户端', '通过使用JITClientTK插件访问管理页时，IE崩溃', '2015-10-29 00:00:00', '已完成', '祝连海', '62618866', '2015-11-17 00:00:00', '0', '任涛', 'NULL', '2015-11-2\n1. 明确问题，重现问题\n2. 提供了新版本的插件安装包后问题没有重现,,等再进一步测试其他PC机后确定问题是否解决\n2015-11-9\n上周没有测试上，后天会和客户一起到现场测试\n2015-11-13\n到现场调试，通过windbg调试发现驱动库中调用unreloaded的方法的时候参数错误，已经告知驱动方\n', 'NULL', 'NULL', '2015-10-29 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
