INSERT INTO `t_project` VALUES ('271', '吉林省信访局', 'NULL', null, null, null, '3.0.23.1', '网关_旁路_报文', '刷新页面后，会提示证书认证数据不完整', '2015-11-16 00:00:00', '已完成', '代艳明', '62618866', '2015-11-19 00:00:00', '0', '杜海洋', 'NULL', '11/30随机数生成的servlet存在并发问题，改成同步方法\n12/28询问应用：多次退出/登录后还会报错', 'NULL', 'NULL', '2015-11-16 00:00:00', '2016-06-08 00:00:00', null, null, '2016-06-08 00:00:00');
