INSERT INTO `t_project_cp` VALUES ('31', '新疆网安', 'NULL', null, null, null, 'NULL', '网关_代理', '新疆WA目前使用的是我公司的E网关，主要实现WA网与GA网之间的访问\n \n      目前出现部分终端无法使用域名访问GA网资源的问题，但这些终端的配置与可\n \n      使用域名访问终端的配置一样，包括终端安装的相关软件也一致，是否有什么有效\n \n      的排查办法可以查找一下问题原因，用户表示如果这种情况影响到WA用户的工作\n \n      将会考虑将我公司的网关撤换掉。\n', '2015-01-26 00:00:00', '已完成', '冯吉喆', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '添加一个dns应用', 'NULL', 'NULL', null, null, null, null, '2016-06-08 00:00:00', null, null);
