INSERT INTO `t_project_cp` VALUES ('174', '人文科工', 'NULL', null, null, null, '3.0.23.1', '网关_服务器', '1、agent应用，IE认证提示安装插件\n2、HA数据同步失败\n3、堡垒机应用传递属性失败', '2015-06-30 00:00:00', '已完成', '代艳明', '62618866', '2015-07-16 00:00:00', '0', '徐昌', '18600056736.0', '7-3：到现场升级网关后问题一解决。问题二未测试（网关不在线）。问题三通过测试环境测试正常，等待工程联系厂商和用户到现场联测。\n7-16：确认是属性传递模板配置错误。修正后解决。', 'NULL', 'NULL', null, null, null, null, '2016-06-08 00:00:00', null, null);
