INSERT INTO `t_bug` VALUES ('662', '打开', '刘宗恺', 'TECH', 'DEV', '使用windows 2000 登录portal页时页面报脚本错误', '<html><body>操作步骤<br>1输入portal页地址登录，客户端未安装。<br>预期结果<br>可以正常登录portal页。<br>登录后可以自动安装客户端。<br>portal页面显示的内容正确<br>实际结果<br>登录portal页时报脚本错误<br>登录客户端后不提示安装插件同时也不自动下载安装客户端。<br>portal页面的内容显示混乱。</body></html>', 'dev-commonts', '1', 'serity', 'priority', '丁利荣', '2014-03-13', '3.0.17.1', 'Phoenix', 'Client');
