INSERT INTO `t_bug` VALUES ('683', '打开', '索瑞军', 'TECH', 'DEV', '恢复出厂设置 不应该恢复 机器上配好的IP地址', '<html><body><font color=#000080><b>测试集:                       </b></font> 王毅<br><font color=#000080><b>测试:                         </b></font> [1]管理员页面定制<br><font color=#000080><b>运行:                         </b></font> Run_3-24_9-42-55<br> <br><font color=#000080><b>测试参数:                     </b></font><br> <br><font color=#000080><b>步骤:                         </b></font> 步骤 3<br> <br><font color=#000080><b>描述:                         </b></font><br>测试目的：恢复出厂值<br>操作步骤：<br>1、进入管理员页面定制页面<br>2、击恢复出厂值<br>3、检查提示信息<br>4、刷新当前页面<br> <br><font color=#000080><b>预期:                         </b></font><br>步骤3的提示信息正确<br>步骤4，管理页面的标题和图片都恢复成出厂值<br> <br><font color=#000080><b>实际: 恢复出厂设置 不应该恢复 机器上配好的IP地址,恢复IP地址严重的影响了用户的使用便捷性，打个比方说：如果用户将服务器托管给第三方，那么需要耗费多大的精力才能够将IP地址改回来。</b></font></body></html>', 'dev-commonts', '1', 'serity', 'priority', '王毅', '2014-03-24', '3.0.17.3', 'Phoenix', 'Client');
