INSERT INTO `t_bug` VALUES ('707', '打开', '平台', 'TECH', 'DEV', '功能描述冲突，不能够多个可信CA 信息导出，也不应该提供多个CA信息的导入', '<html><body><font color=#000080><b>测试集:                       </b></font> 王毅<br><font color=#000080><b>测试:                         </b></font> [1]导入<br><font color=#000080><b>运行:                         </b></font> Run_3-25_18-19-28<br> <br><font color=#000080><b>测试参数:                     </b></font><br> <br><font color=#000080><b>步骤:                         </b></font> 步骤 3<br> <br><font color=#000080><b>描述:                         </b></font><br>测试目的：导入含有多个CA的正确文件<br>前提：CA1配置了CRl文件、自动下载CRl文件，<br>ca2配置了ocsp校验，连接失败时，使用CRL<br>操作步骤：<br>1、点击导入，进入导入页面<br>2、导入正确文件<br>3、用该CA下的证书访问应用<br> <br><font color=#000080><b>预期:                         </b></font><br>导入成功，该ca下的用户正常使用网关<br> <br><font color=#000080><b>实际:  功能描述冲突，不能够多个可信CA 信息导出，也不应该提供多个CA信息的导入</b></font></body></html>', 'dev-commonts', '1', 'serity', 'priority', '王毅', '2014-03-26', '3.0.17.3', 'Phoenix', 'Client');
