INSERT INTO `t_bug` VALUES ('714', '打开', '平台', 'TECH', 'DEV', '日志删除归档操作没有相应的管理日志记录', '<html><body><font color=#000080><b>测试集:                       </b></font> 王毅<br><font color=#000080><b>测试:                         </b></font> [1]手动归档<br><font color=#000080><b>运行:                         </b></font> Run_3-26_14-14-29<br> <br><font color=#000080><b>测试参数:                     </b></font><br> <br><font color=#000080><b>步骤:                         </b></font> 步骤 8<br> <br><font color=#000080><b>描述:                         </b></font><br>测试目的：手动归档-删除日志<br><br>操作步骤：<br>1.归档类型：手动归档<br>  有效期设置：2天<br>  归档操作：删除<br>2.点击确定按钮<br> <br><font color=#000080><b>预期:                         </b></font><br>提示操作成功<br>日志表里符合条件的日志全部被删除<br> <br><font color=#000080><b>实际: 日志删除归档操作没有相应的管理日志记录</b></font></body></html>', 'dev-commonts', '1', 'serity', 'priority', '王毅', '2014-03-26', '3.0.17.3', 'Phoenix', 'Client');
