INSERT INTO `t_bug` VALUES ('801', '打开', '邢世宇', 'TECH', 'DEV', 'portal页自学习功能不好用客户端自学习可以学习但是无法清除自学习。', '<html><body><b><font color=#000080>测试集:                       </font></b> 丁利荣<p><b><font color=#000080>测试:                         </font></b> [1]BS应用代填场景<p><b><font color=#000080>运行:                         </font></b> Run_5-7_14-15-44<p> <p><b><font color=#000080>测试参数:                     </font></b><p> <p><b><font color=#000080>步骤:                         </font></b> 步骤 4<p> <p><b><font color=#000080>描述:                         </font></b><p><html><body>测试目的：用户自学习应用账号信息，BS应用的自动登录功能<br>测试步骤：<br>1、配置自动登录BS应用（测试环境中自动登录的7种场景）<br>2、应用结合方式选择&quot;应用无法改造，使用自动登录&quot;<br>   自动登录类型选择&quot;使用自学习功能&quot;，正确配置应用账号登录URL信息<br>4、通过portal和客户端访问BS应用，使用正确口令登录一次。<br>5、再次通过portal和客户端访问BS应用</body></html><p> <p><b><font color=#000080>预期:                         </font></b><p><html><body>验证结果：<br>都可以使用已登录过的账号信息实现自动登录</body></html><p> <p><b><font color=#000080>实际:                         </font></b><p></body></html>', 'dev-commonts', '1', 'serity', 'priority', '丁利荣', '2014-05-07', '3.0.18.4', 'Phoenix', 'Client');
