INSERT INTO `t_bug` VALUES ('1049', '打开', '平台', 'TECH', 'DEV', 'HA 虚拟IP访问管理页面的时候 如果 主备机 都存在 访问的是 备机页面', '<html><body><font color=#000080><b>测试集:                       </b></font> 冯彩英<br><font color=#000080><b>测试:                         </b></font> [1]HA<br><font color=#000080><b>运行:                         </b></font> Run_7-11_17-33-23<br> <br><font color=#000080><b>测试参数:                     </b></font><br> <br><font color=#000080><b>步骤:                         </b></font> 步骤 3<br> <br><font color=#000080><b>描述:                         </b></font><br>测试目的：主机和备机是否可以正常切换，文件同步IP 为空<br>步骤（需要两台机器操作）：A为主机B为备机<br>1 进入高可用性页面，工作方式选择 HA配置<br>2  正确配置主机和备机的HA，点击保存<br>3 重启主机和备机，用虚拟ip访问管理页面<br>4 关闭主机或者拔掉主机网线，用虚拟ip访问管理页面<br>5 等待主机服务恢复正常，用虚拟ip访问管理页面<br>6 关闭主机的ha功能，并重新启动机器，用虚拟ip访问<br>7 关闭备机的ha功能，并重新启动机器，用虚拟ip访问<br> <br><font color=#000080><b>预期:                         </b></font><br>步骤3 用虚拟ip访问管理页面，查看到是主机的信息<br>    4 用虚拟ip访问管理页面，查看到是备机信息<br>    5 用虚拟ip访问管理页面，查看到是主机的信息<br>    6 用虚拟ip访问管理页面，查看到是备机信息<br>    7 虚拟ip无法访问<br> <br><font color=#000080><b>实际:HA 虚拟IP访问管理页面的时候 如果 主备机 都存在 访问的是 备机页面</b></font></body></html>', 'dev-commonts', '1', 'serity', 'priority', '王毅', '2014-07-11', '3.0.20.2', 'Phoenix', 'Client');
