INSERT INTO `t_bug` VALUES ('1170', '打开', '王毅', 'TECH', 'DEV', 'IE11访问主路BS正向自动登录应用时无法实现自动登录', '<html><body>测试环境：<br>操作系统：Win7_X64；<br>浏览器IE11；<br><br>操作步骤：<br>1.添加主路，BS正向自动登录应用；<br>2.以系统管理员登录portal页面，在应用账户管理中新建从帐号名称和密码；<br>3.portal页或客户端以系统管理员证书登录并访问此应用；<br><br>预期结果：<br>实现自动登录；<br>实际结果：<br>不能实现自动登录；<br><br>备注：<br>IE8，IE9，IE10均能实现自动登录；<br>PNXSSODataMgr.log中已记录自动登录应用的数据；</body></html>', 'dev-commonts', '1', 'serity', 'priority', '冯彩英', '2014-11-05', '3.0.22.5', 'Phoenix', 'Client');
