INSERT INTO `t_bug` VALUES ('1745', '打开', '索瑞军', 'TECH', 'DEV', '公告信息填写英文单引号后无法成功保存', '<html><body><font color=#000080><b>测试集:                       </b></font> 朱虹宇<br><font color=#000080><b>测试:                         </b></font> [1]01portal页面配置<br><font color=#000080><b>运行:                         </b></font> Run_9-24_14-49-40<br> <br><font color=#000080><b>测试参数:                     </b></font><br> <br><font color=#000080><b>步骤:                         </b></font> 步骤 3<br> <br><font color=#000080><b>描述:                         </b></font><br>测试目的：验证portal页面配置<br>前置条件：通过管理员身份登录到管理配置页面，<br>测试步骤：<br>在portal页配置页面中，公告信息输入域输入正确信息，<br>查看公告信息输入域下的字数上限联动提示<br>点击保存<br> <br><font color=#000080><b>预期:                         </b></font><br>验证结果：<br>输入信息后，公告信息输入域下的字数上限联动提示准确无误<br>当界面中其他必填项已填写完毕后，点击保存后保存成功<br>登录portal页验证公告信息<br> <br><font color=#000080><b>实际:                         </b></font><br><font color=#000080><b>在公告内容中填写英文的单引号后无法成功保存</b></font></body></html>', 'dev-commonts', '1', 'serity', 'priority', '朱虹宇', '2015-09-24', '3.0.27.10', 'Phoenix', 'Client');
