INSERT INTO `t_bug` VALUES ('3081', '打开', '史志伟', 'TECH', 'DEV', '客户端登录后访问Agent应用，客户端单点失效。', '<html><body>操作步骤：<br>1、网关中添加正向、反向、透明和Agent应用<br>2、登录客户端<br>3、在客户端上双击Agent应用<br>预期结果：<br>不需要认证，直接使用登录客户端的证书进行认证<br>实际结果：<br>无法实现单点。</body></html>', 'dev-commonts', '1', 'serity', 'priority', '于凯', '2016-12-23', '3.0.33.3', 'Phoenix', 'Client');
INSERT INTO `t_bug` VALUES ('3082', '新建', '索瑞军', 'TECH', 'DEV', '定制网关portal登录页logo，不上logo图片可以正常保存，导致在portal登录页logo显示为缺省图标', '<html><body>操作步骤：<br>1.登录网关服务端，选择【系统门户】【登录页定制】<br>2.选择&quot;Logo定制&quot;--保存<br>预期结果：<br>不上传Logo图片，点击保存，保存配置失败并弹出错误提示信息<br>实际结果：<br>不上传Logo图片，可以正常保存，浏览器访问网关portal页，登录页logo显示为缺省图标（见附件）</body></html>', 'dev-commonts', '1', 'serity', 'priority', '霍成义', '2016-12-23', '3.0.33.3', 'Phoenix', 'Client');
