INSERT INTO `t_bug` VALUES ('3534', '新建', '王毅', 'TECH', 'DEV', '系统设置-设备编号配置在输入设备编号配置的过程中点击2次enter后显示白页', '<html><body>操作步骤<br>1使用管理员登录网关服务器后台<br>2选择【系统设置】-【设备编号配置】进入<br>3在设备编号配置中输入内容后光标不离开输入框双击&quot;enter&quot;键。<br>预期结果<br>无任任何影响<br>实际结果<br>页面显示白页</body></html>', 'dev-commonts', '1', 'serity', 'priority', '丁利荣', '2017-05-22', '3.0.34.4', 'Phoenix', 'Client');
