INSERT INTO `t_project` VALUES ('17', '内蒙省委组织部', 'NULL', '3.0.2.3', '网关_旁路_报文', '内蒙省委组织部应用接入遇到问题，问题如下：\n应用测试没有问题，部署到正式环境中，在访问应用后，部分用户出现附件1的问题，应用厂商查看代码发现是附件2中，咱们提供的代码报的错。\n身份认证网关版本：V3.0.2.3      接入模式为：旁路、报文、BS，应用系统为java编写。\n今天下午用户那边等着给用户演示，希望尽快能把问题解决，\n谢谢!!!\n', '2015-01-16 00:00:00', '已完成', '代艳明', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', '2016-06-08 00:00:00', null);
