INSERT INTO `t_project` VALUES ('68', '江西CA', 'NULL', 'NULL', '网关_移动', '江西CA将开展移动业务，近期会有项目落地，应用模式为E网关与蓝牙KEY的结合。\n \n江西CA现确定项目中采用龙脉KEY（支持双算法），测试蓝牙KEY已经提供。\n \n客户要求先行完成RSA证书与网关的结合，后期随着SM2证书的推广，再行实现SM2证书的发放、网关的结合。\n \n江西CA是我们长期的合作伙伴，随着移动业务后期拓展，我们的网关产品将会有更多的售卖机会。\n \n请评估并协调尽快处理。\n', '2015-03-13 00:00:00', '已完成', '王建平', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', '新需求', 'NULL', 'NULL', '2016-06-08 00:00:00', null);
