INSERT INTO `t_project` VALUES ('99', '中组部', 'NULL', '3.0.21.5', '磐石_客户端', '一台终端计算机无法绑定KEY，尝试修复磐石客户端，在修复过程中出现报错，无法完成修复。', '2015-04-14 00:00:00', '已完成', '冯吉喆', '62618866', '2015-04-16 00:00:00', '0', '李旭锐', 'NULL', '解决方案是：提供一个磐石卸载工具（见附件），分为32位和64位的，通过卸载工具卸载之后，重新安装客户端再测试下，卸载工具直接双击运行即可，需要重启计算机，然后再安装客户端。', 'NULL', 'NULL', '2016-06-08 00:00:00', null);
