INSERT INTO `t_project` VALUES ('102', '南方电视台', 'NULL', '3.0.29.25', '磐石_客户端', '1、XP系统安装了磐石终端安全系统后，绑定了一个U-KEY成功；\n2、另外一个U-KEY插进来，同时帮定成功后，拔出U-KEY，插回第一次绑定成功的U-KEY，点击刷新证书无法刷新，也就是认不到第一次绑定的U-Key；\n3、win7系统的话有切换用户能够解决这个问题，但是XP无法切换用户 \n', '2015-04-14 00:00:00', '已完成', '刘志钢', '62618866', '2015-04-15 00:00:00', '0', '张工（中软）、谢泽勇', '张工电话：15013262820', '服务器上修改策略（“禁止绑定到同一windows的账号解锁计算机”去掉勾选），客户端更新策略，问题已解决。', 'NULL', 'NULL', '2016-06-08 00:00:00', null);
INSERT INTO `t_project` VALUES ('103', '辽宁国土资源厅', 'NULL', '3.0.24.4', '网关_旁路_报文', 'ASPX_DEMO搭建的时候，认证后报错', '2015-04-17 00:00:00', '已完成', '代艳明', '62618866', '2015-05-06 00:00:00', '0', '杜海洋', 'NULL', '工程配置有误，低版本的IIS下，网站名称，应用名称必须是ASPXBS_Demo,否则报404.\n5-6:demo我发访问问题换台服务器已经解决；CSP问题之前遇到过，是系统问题，和网关产品无关。', 'NULL', 'NULL', '2016-06-08 00:00:00', null);
