INSERT INTO `t_project` VALUES ('181', '辽宁省公安厅', 'NULL', '3.0.21.2', '网关_旁路_报文', '辽宁省公安厅灾备中心新上的身份认证网关在认证成功后获取的属性信息没有属性值，而之前的网关有属性值。', '2015-07-07 00:00:00', '已完成', '刘莎', '62618866', '2015-07-07 00:00:00', '0', '邹建平', 'NULL', '7月7日：用户信息配置--》用户属性源配置--》高级配置：在老版的网关中属性值返回类型只有名称和编码，新版的传值的选择“名称+编码”属性值才传递成功！', 'NULL', 'NULL', '2016-06-08 00:00:00', null);
