INSERT INTO `t_bug` VALUES ('3260', '重新打开', '丁为厂', 'TECH', 'DEV', '网关开启用户安全策略，使用国密KEY证书用户访问网关Portal页，输入错误的PIN码登录或不输入PIN码取消，弹出错误提示信息描述不准确', '<html><body>预置条件：<br>1.后台开启用户安全策略功能（SSH连接到网关，将/home/gateway/config目录下menu.xml文件中的用户安全策略注释删除）<br>操作步骤：<br>1.登录网关，选择【用户管理】【用户安全策略】，选择高级规则，密码重试次数5，密码修改周期1<br>2.打开浏览器访问网关Portal页，使用国密key证书用户登录，在弹出PIN码框输入错误的PIN码登录或不输入PIN码点击取消<br>预期结果：<br>弹出错误提示信息<br>实际结果：<br>弹出错提示信息描述不准确（见附件）<br>在弹出PIN码框不输入PIN码，点击取消，提示用户取消操作<br>在弹出PIN码框输入错误PIN码，点击登录，提示用户PIN码错误</body></html>', 'dev-commonts', '1', 'serity', 'priority', '霍成义', '2017-02-28 00:00:00', '3.0.33.6', 'Phoenix', 'Client', '2017-07-13 12:57:49');
