INSERT INTO `t_bug` VALUES ('29', '新建', '王毅', 'TECH', 'DEV', '客户端配置中，允许访问的例外列表中，可以添加两条完全相同的记录，系统没有给出提示', '<html><body>操作步骤：<br>1.登录网关，选择【客户端管理】【客户端配置】，开启阻止上网<br>2.允许访问的例外列表中，添加两条一样例外地址（一样IP或IP端）<br>3.点击保存<br>预期结果:                     <br>弹出提示信息，提示用户例外地址重复<br>实际结果:     <br>没有提示信息，可以正常保存</body></html>', 'dev-commonts', '1', 'serity', 'priority', '霍成义', '2017-05-12 00:00:00', '3.0.34.3', 'Phoenix', 'Client', '2017-07-13 13:24:47');
