INSERT INTO `t_bug` VALUES ('3711', '新建', '王毅', 'TECH', 'DEV', '信息传递模板管理-自定义属性配置，已经添加一条自定义属性在列表中双击关键字，修改关键字信息中包含中文和空客时弹出提示并且同时返回到信息传递模板管理页面。', '<html><body>测试前提<br>已经添加一条自定义属性<br>操作步骤<br>1选择【应用管理】【信息传递模板管理】【自定义属性配置】进入到自定义属性列表页面。<br>2选择一条自定义属性信息鼠标双击关键字信息修改关键字内容<br>3输入数字（中文）和空格后点击确定弹出&quot;输入的关键字[23 3 3 3 3]含有中文，全角符号或空格，请重新设置！&quot;<br>预期结果<br>弹出错误提示信息后页面应该还停留在自定义属性列表页面。<br>实际结果<br>弹出错误提示信息后页面跳转到信息传递模板管理页面。</body></html>', 'dev-commonts', '1', 'serity', 'priority', '丁利荣', '2017-06-23 00:00:00', '3.0.34.7', 'Phoenix', 'Client', '2017-08-08 13:14:23');
