INSERT INTO `t_project` VALUES ('39', '龙源电力', '军工1', null, null, null, 'NULL', '网关_旁路_报文', '报文模式接入，用户要在应用页面上显示证书主题和输入密码，\n一次性传递给咱们的控件进行认证。类似网银的操作，而非我们传统改造模式那样弹出证书对话框。请确认我们的报文改造模式是否提供这样的接口。如果需要进一步了解用户需求，可与孟飞联系。', '2015-01-30 00:00:00', '已完成', '代艳明', '62618866', '1970-01-01 00:00:00', '0', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', '2016-06-08 00:00:00', '2016-12-31 00:00:00', null, null);
