INSERT INTO `t_project` VALUES ('269', '吉林社保', '军工1', null, null, null, 'NULL', '网关_旁路_Agent', '1.AGENT部署应用，通过客户端登陆应用需要二次输入PIN码，正常客户端应该通过认证后访问应用就不需要再次认证了。                                         2.客户端默认调用IE浏览器访问应用，客户想换成谷歌浏览器。                    \n3.谷歌浏览器与应用结合是否稳定，是否经过测试？  ', '2015-11-19 00:00:00', '已完成', '祝连海', '62618866', '2015-11-19 00:00:00', '0', '杜海洋', '15843011925.0', '2015-11-19\n目前网关及客户端版本不支持Agent应用的非IE浏览器单点登录，新版本能满足项目需求，问题的解决需要等待新版本的发布。', 'NULL', 'NULL', '2016-06-08 00:00:00', '2016-12-31 00:00:00', null, null);
